using CopaceticSoftware.Common.Patterns;
using CopaceticSoftware.pMixins.CodeGenerator.Pipelines.GenerateCode.Steps.MixinWrappersGenerator;
using ICSharpCode.NRefactory.CSharp;

namespace CopaceticSoftware.pMixins.CodeGenerator.Pipelines.GenerateCode.Steps.PreClassGeneration
{
    /// <summary>
    /// Generates a container class for Auto Generated class directly in the Target code-behind class:
    /// <code>
    /// <![CDATA[
    /// private class __pMixinAutoGenerated
    /// {
    /// }    
    /// ]]>
    /// </code>
    /// </summary>
    /// <remarks>
    /// Child classes are generated in <see cref="GenerateMixinSpecificAutoGeneratedClass"/>
    /// </remarks>
    public class GenerateAutoGeneratedContainerClass : IPipelineStep<pMixinGeneratorPipelineState>
    {
        public const string AutoGeneratedClassName = "__pMixinAutoGenerated";

        public bool PerformTask(pMixinGeneratorPipelineState manager)
        {
            var autoGeneratedClass = new TypeDeclaration
            {
                ClassType = ClassType.Class,
                Modifiers = Modifiers.Private | Modifiers.Sealed,
                Name = AutoGeneratedClassName,
            };

            manager.AutoGeneratedContainerClass =
                manager.GeneratedClass.AddNestedType(autoGeneratedClass);

            return true;
        }
    }
}
