using CopaceticSoftware.CodeGenerator.StarterKit.Extensions;
using CopaceticSoftware.Common.Patterns;
using ICSharpCode.NRefactory.CSharp;

namespace CopaceticSoftware.pMixins.CodeGenerator.Pipelines.GenerateCode.Steps.pMixinClassLevelGenerator.Steps
{
    /// <summary>
    /// Generates the Mixin specific 'auto-generated' container class.  These live 
    /// underneath the __pMixinAutoGenerated class and (can) store the Abstract and Protected Members
    /// wrappers. 
    /// </summary>
    /// <example>
    /// <code>
    /// <![CDATA[
    /// //Parent - Generated in other step
    /// private class __pMixinAutoGenerated
    /// {
    ///     //GenerateMixinSpecificAutoGeneratedClass
    ///     //Container for Test.Test_MixinWithMultipleConstructors
    ///     public sealed class Test_MixinWithMultipleConstructors
    ///     {
    ///     }
    /// }    
    /// ]]>
    /// </code>
    /// </example>
    /// <remarks>  
    /// Parent class is generated in <see cref="GenerateAutoGeneratedContainerClass"/>.
    /// Child classes are generated in <see cref="GenerateAbstractMixinMembersWrapperClass"/> 
    /// and <see cref="GenerateProtectedMixinMembersWrapperClass"/>.
    /// </remarks>
    public class GenerateMixinSpecificAutoGeneratedClass : IPipelineStep<pMixinGeneratorPipelineState>
    {
        public static string GetMixinSpecificAutoGeneratedClassName(pMixinGeneratorPipelineState manager)
        {
            return manager.CurrentpMixinAttribute.Mixin
                .GetFullNameAsIdentifier()
                .Replace(".", "_");
        }

        public static string GetFullNameForChildType(pMixinGeneratorPipelineState manager, string childTypeName)
        {
            return
                string.Format("{0}.{1}.{2}",
                    GenerateAutoGeneratedContainerClass.AutoGeneratedClassName,
                    GetMixinSpecificAutoGeneratedClassName(manager),
                    childTypeName);
        }

        public bool PerformTask(pMixinGeneratorPipelineState manager)
        {
            var mixinSpecificAutoGeneratedClassDeclaration = new TypeDeclaration
            {
                ClassType = ClassType.Class,
                Modifiers = Modifiers.Public | Modifiers.Sealed,
                Name = GetMixinSpecificAutoGeneratedClassName(manager)
            };

            manager.CurrentAutoGeneratedTypeDeclaration = 
                manager.AutoGeneratedContainerClass.AddNestedType(mixinSpecificAutoGeneratedClassDeclaration);

            return true;

        }
    }
}
