using CopaceticSoftware.Common.Patterns;
using CopaceticSoftware.pMixins.CodeGenerator.Infrastructure.CodeGenerationPlan;
using ICSharpCode.NRefactory.CSharp;

namespace CopaceticSoftware.pMixins.CodeGenerator.Pipelines.GenerateCodeBehind.Pipelines.MixinLevelCodeGenerator.Steps.CreateTypeDeclarations
{
    /// <summary>
    /// Creates the <see cref="MixinLevelCodeGeneratorPipelineState.ExternalMixinSpecificAutoGeneratedNamespace"/>
    /// based off of <see cref="MixinGenerationPlan.ExternalMixinSpecificAutoGeneratedNamespaceName"/>.  This 
    /// is the container for external auto generated classes and interfaces, such as Abstract / Protected Wrappers
    /// and Requirement Interfaces.
    /// <code>
    /// <![CDATA[
    /// namespace pMixins.AutoGenerated.Namespace.For.Mixin.Mixin{}
    /// ]]>
    /// </code>
    /// </summary>
    public class CreateExternalMixinSpecificAutoGeneratedNamespace : IPipelineStep<MixinLevelCodeGeneratorPipelineState>
    {
        public bool PerformTask(MixinLevelCodeGeneratorPipelineState manager)
        {
            manager.ExternalMixinSpecificAutoGeneratedNamespace =
                new NamespaceDeclaration(
                    manager.MixinGenerationPlan.ExternalMixinSpecificAutoGeneratedNamespaceName);

            return true;
        }
    }
}
