using CopaceticSoftware.Common.Patterns;
using CopaceticSoftware.pMixins.CodeGenerator.Infrastructure.CodeGenerationPlan;
using ICSharpCode.NRefactory.CSharp;

namespace CopaceticSoftware.pMixins.CodeGenerator.Pipelines.GenerateCodeBehind.Pipelines.TargetLevelCodeGenerator.Steps.CreateTypeDeclarations
{
    /// <summary>
    /// Creates the <see cref="TargetLevelCodeGeneratorPipelineState.ExternalTargetSpecificAutoGeneratedNamespace"/>
    /// based off of <see cref="CodeGenerationPlan.ExternalTargetSpecificAutoGeneratedNamespaceName"/>.  This 
    /// is the container for external auto generated classes and interfaces such as the 
    /// shared requirements interface.
    /// <code>
    /// <![CDATA[
    /// namespace pMixins.AutoGenerated.Namespace.For.Target{}
    /// ]]>
    /// </code>
    /// </summary>
    public class CreateExternalTargetSpecificAutoGeneratedNamespace : IPipelineStep<TargetLevelCodeGeneratorPipelineState>
    {
        public bool PerformTask(TargetLevelCodeGeneratorPipelineState manager)
        {
            manager.ExternalTargetSpecificAutoGeneratedNamespace =
               new NamespaceDeclaration(
                   manager.CodeGenerationPlan.ExternalTargetSpecificAutoGeneratedNamespaceName);

            return true;
        }
    }
}
